import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Button, Form } from 'react-bootstrap';

function EditTime() {
  const navigate = useNavigate();
  const location = useLocation();
  const [times, setTimes] = useState(location.state.playerTimes);

  const handleTimeChange = (name, newTime) => {
    setTimes(prevTimes => ({ ...prevTimes, [name]: newTime }));
  };

  const handleSubmit = () => {
    // Save the edited times back to localStorage or context
    localStorage.setItem('playerTimes', JSON.stringify(times));
    navigate('/end-game');
  };

  return (
    <div className="edit-time">
      <h2>Edit Player Times</h2>
      <Form onSubmit={handleSubmit}>
        {Object.entries(times).map(([name, time]) => (
          <Form.Group key={name} className="mb-3">
            <Form.Label>{name}'s Time:</Form.Label>
            <Form.Control
              type="text"
              defaultValue={time}
              onChange={(e) => handleTimeChange(name, e.target.value)}
            />
          </Form.Group>
        ))}
        <Button type="submit" variant="primary">Save Changes</Button>
      </Form>
      <Button variant="secondary" onClick={() => navigate(-1)}>Back</Button>
    </div>
  );
}

export default EditTime;
