import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useGame } from './GameContext';
import './PickTeam.css';  // Make sure to create this CSS file

function PickTeam() {
    const { team, setGameTeam } = useGame();
    const [selectedPlayers, setSelectedPlayers] = useState([]);
    const navigate = useNavigate();

    const handleSelectPlayer = (player) => {
        const isAlreadySelected = selectedPlayers.includes(player);
        if (isAlreadySelected) {
            setSelectedPlayers(selectedPlayers.filter(p => p !== player));
        } else {
            setSelectedPlayers([...selectedPlayers, player]);
        }
    };

    const handleStartGame = () => {
        setGameTeam(selectedPlayers);
        navigate('/timer');
    };

    return (
        <div className="pick-team">
            <h1>Select Players for the Game</h1>
            <div className="player-list">
                {team.map(player => (
                    <div key={player.name} className="player-item">
                        <label className="player-label">
                            <input
                                type="checkbox"
                                checked={selectedPlayers.includes(player)}
                                onChange={() => handleSelectPlayer(player)}
                            />
                            {player.name}
                        </label>
                    </div>
                ))}
            </div>
            <Button onClick={handleStartGame} className="start-game-btn">Start Game</Button>
        </div>
    );
}

export default PickTeam;
